package 데이타;

public class 기본데이터2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//기본 데이터 네가지: 정수, 실수, 문자, 논리
		//기호=> 연산자
		
		int age = 100;
		double height = 181.2;
		char gender = '남';
		boolean food = true; // false
		
		System.out.println("내 나이는 " + age); //+결합연산자
		System.out.println("내 키는 " + height);
		System.out.println("성별은 " + gender);
		System.out.println(food);
		
		//글자들 : 문자+문자+문자+...(여러글자, 문자열, string)
	}

}
