package 연산자;

public class 비교연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 200;
		int y = 100;
		System.out.println("동일? " + (x == y));
		System.out.println("틀림? " + (x != y));
		System.out.println("크거나같음? " + (x >= y));
		System.out.println("큼? " + (x > y));
	}

}
